AFAnalysisRule.ConfigString:
DepthChange := (Abs(TagVal('Measured Depth','*')) - Abs(PrevVal('Measured Depth','*-2h')));
StartTrigger := DepthChange > 0.1  AND 'Top Drive RPM'> 15 AND 'Well State' = "Drilling";
EndTrigger := (DepthChange > 0.1 AND 'Top Drive RPM'< 15 ) or (DepthChange <= 0.1 AND 'Top Drive RPM' > 15 ) or ('Well State' <> "Drilling");

DepthChange := (Abs(TagVal('Measured Depth','*')) - Abs(PrevVal('Measured Depth','*-2h')));
StartTrigger := DepthChange > 0.1 AND 'Top Drive RPM' < 15 AND 'Well State' = "Drilling";
EndTrigger := DepthChange <= 0.1 or 'Top Drive RPM'> 15 or 'Well State' <> "Drilling";

DepthChange := (Abs(TagVal('Measured Depth','*')) - Abs(PrevVal('Measured Depth','*-2h')));
StartTrigger := 'Measured Depth' > 1 AND DepthChange <= 0.1 AND 'Top Drive RPM'> 15  AND 'Well State' = "Drilling";
EndTrigger := DepthChange > 0.1 or 'Well State' <> "Drilling";
